import React, {useState, useEffect} from "react";
import { account } from "../db/appwriteConfig";

const Login = ({ logStatus }) => {
  //Get current user
  const [user, setUser] = useState();

  useEffect(() => {
    account
      .get()
      .then((res) => {
        setUser(res);
      })
      .catch((err) => {
        console.log(err);
        setUser();
      });
  }, []);

  //Handle login
  const handleLogin = () => {
    account.createOAuth2Session("google", "http://localhost:3000");
  };

  //Handle  Logout
  const handleLogout = () => {
    account
      .deleteSession("current")
      .then((res) => {
        window.location.href = "http://localhost:3000";

      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      {logStatus ? (
        <div className="w-100 text-right">
          <p className="mx-5">Hi, {user.name}</p>
          <button onClick={handleLogout} className="btn btn-primary mx-5">
            Logout
          </button>
        </div>
      ) : (
        <div className="w-100 text-right">
          <button onClick={handleLogin} className="btn btn-primary mx-5 my-5">
            Login
          </button>
        </div>
      )}
    </div>
  );
};

export default Login;
