import React, { useState, useEffect } from "react";
import axios from "axios";

const Recipe = () => {
  const [recipes, setRecipes] = useState([]);
  const [newRecipe, setNewRecipe] = useState({
    name: "",
    ingredients: "",
    recipe: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/v1/getRecipes")
      .then(({ data }) => {
        setRecipes(data.documents);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [recipes]);

  const createNewRecipe = (e) => {
    e.preventDefault();

    axios
      .post("http://localhost:5000/api/v1/createRecipe", newRecipe)
      .then((addedRecipe) => {
        console.log(addedRecipe);
        setSuccess(" New Recipe added Successfully !");
      })
      .catch((err) => {
        setError("Failed to add new Recipe !");
        console.log(err);
      });

    setNewRecipe({
      name: "",
      ingredients: "",
      recipe: "",
    });
    setError("");
    setSuccess("");
  };

  const deleteRecipe = (id) => {
    axios
      .delete(`http://localhost:5000/api/v1/deleteRecipe/${id}`)
      .then((res) => {
        alert("Recipe Deleted Successfully !");
      })
      .catch((err) => {
        alert("Uh Oh!, Error Occured : Recipe Could not be deleted !");
      });
  };

  const updateRecipe = (id) => {
    axios
      .patch(`http://localhost:5000/api/v1/updateRecipe/${id}`)
      .then((res) => {
        alert("Recipe updated successfully!");
      })
      .catch((err) => {
        alert("Uh Oh!, Error Occured : Recipe Could not be updated ");
      });
  };

  return (
    <div>
      <div>
        <br />
        <form className="container-lg my-10">
          <h2>Create Recipe</h2>
          <div className="form-group">
            <label for="exampleInputEmail1">Food Name</label>
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
              required
              aria-describedby="emailHelp"
              value={newRecipe.name}
              onChange={(e) => {
                setNewRecipe({
                  ...newRecipe,
                  name: e.target.value,
                });
              }}
            />
          </div>
          <div className="form-group">
            <label for="exampleInputPassword1">Ingredients</label>
            <input
              value={newRecipe.ingredients}
              required
              type="text"
              name="ingredients"
              className="form-control"
              id="ingredients"
              onChange={(e) => {
                setNewRecipe({
                  ...newRecipe,
                  ingredients: e.target.value,
                });
              }}
            />
          </div>

          <div class="form-group">
            <label for="exampleFormControlTextarea1">Recipe</label>
            <textarea
              required
              name="recipe"
              class="form-control"
              id="recipe"
              value={newRecipe.recipe}
              rows="3"
              onChange={(e) => {
                setNewRecipe({
                  ...newRecipe,
                  recipe: e.target.value,
                });
              }}
            ></textarea>
          </div>
          <div>{error && <p className="text-danger"> {error} </p>}</div>
          <div>{success && <p className="text-success"> {success} </p>}</div>
          <button
            type="submit"
            className="btn btn-primary"
            onClick={(e) => {
              createNewRecipe(e);
            }}
          >
            Add New Recipe
          </button>
        </form>
      </div>

      {/* Table */}
      <div>
        <br />
        <div className="container-lg  ">
          <h2>Recipes</h2>
          <table className="table table-bordered">
            <thead className="thead-dark">
              <tr>
                <th scope="col">Food Name</th>
                <th scope="col">Ingredients</th>
                <th scope="col">Recipe</th>
                <th scope="col">Actions</th>
              </tr>
            </thead>
            <tbody>
              {recipes.map((recipe) => (
                <tr>
                  <td>{recipe.name}</td>
                  <td>{recipe.ingredients}</td>
                  <td>{recipe.recipe}</td>

                  <td className="d-flex border-bottom-0 border-right-0 border-left-0">
                    <button
                      onClick={() => deleteRecipe(recipe.$id)}
                      type="button"
                      class="btn btn-danger mx-1"
                    >
                      <i class="fas fa-trash "></i>
                    </button>
                    <button
                      onClick={() => {
                        updateRecipe(recipe.$id);
                      }}
                      type="button"
                      class="btn btn-success mx-1"
                    >
                      <i class="fas fa-pen"></i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Recipe;
