import React, { useState } from "react";
import axios from "axios";

const AddRecipe = () => {
  const [newRecipe, setNewRecipe] = useState({
    name: "",
    ingredients: "",
    recipe: "",
  });

  const createNewRecipe = (e) => {
    e.preventDefault();

    axios
      .post("http://localhost:5000/api/v1/createRecipe", newRecipe)
      .then((addedRecipe) => {
        console.log(addedRecipe);
      location.reload();
      })
      .catch((err) => {
        console.log(err);
        <div
          className="toast"
          role="alert"
          aria-live="assertive"
          aria-atomic="true"
        >
          <div className="toast-header">
            <img src="..." className="rounded mr-2" alt="..." />
            <strong className="mr-auto">Bootstrap</strong>
            <small>11 mins ago</small>
            <button
              type="button"
              className="ml-2 mb-1 close"
              data-dismiss="toast"
              aria-label="Close"
            >
              <span aria-hidden="true">Ã—</span>
            </button>
          </div>
          <div className="toast-body">
            Hello, world! This is a toast message.
          </div>
        </div>;
      });

    setNewRecipe({
      name: "",
      ingredients: "",
      recipe: "",
    });
  };

  return (
    <div>
      <br />
      <form className="container-lg my-10">
        <h2>Create Recipe</h2>
        <div className="form-group">
          <label for="exampleInputEmail1">Food Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            required
            aria-describedby="emailHelp"
            value={newRecipe.name}
            onChange={(e) => {
              setNewRecipe({
                ...newRecipe,
                name: e.target.value,
              });
            }}
          />
        </div>
        <div className="form-group">
          <label for="exampleInputPassword1">Ingredients</label>
          <input
            value={newRecipe.ingredients}
            required
            type="text"
            name="ingredients"
            className="form-control"
            id="ingredients"
            onChange={(e) => {
              setNewRecipe({
                ...newRecipe,
                ingredients: e.target.value,
              });
            }}
          />
        </div>

        <div class="form-group">
          <label for="exampleFormControlTextarea1">Recipe</label>
          <textarea
            required
            name="recipe"
            class="form-control"
            id="recipe"
            value={newRecipe.recipe}
            rows="3"
            onChange={(e) => {
              setNewRecipe({
                ...newRecipe,
                recipe: e.target.value,
              });
            }}
          ></textarea>
        </div>
<div> {error && (<p className="text-danger"> Failed to add new Recipe ! </p>)} </div>
<div> {success && (<p className="text-success"> New Recipe added Successfully ! </p>)} </div>
        <button
          type="submit"
          className="btn btn-primary"
          onClick={(e) => {
            createNewRecipe(e);
          }}
        >
          Add New Recipe
        </button>
      </form>
    </div>
  );
};

export default AddRecipe;
