import axios from "axios";

const RecipesTable = () => {
  const [food, setRecipes] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/")
      .then((recipeData) => {
        setRecipes(recipeData.data.documents)
        console.log(food);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div>
      <br />
      <div className="container-lg">
        <h2>Recipes</h2>
        <table className="table table-bordered">
          <thead className="thead-dark">
